  currentId?: number | null;
}>();

const emit = defineEmits<{
  (
    e: "regionHover",
    payload: {
      id: number | null;
      x: number;
      y: number;
    }
  ): void;
}>();

const regionHover = (id: number | null, event?: MouseEvent) => {
  if (!event || id === null) {
    emit("regionHover", {
      id: null,
      x: 0,
      y: 0,
    });
    return;
  }

  emit("regionHover", {
    id,
    x: event.clientX,
    y: event.clientY,
  });
};
